<?php

namespace Ens\LunchBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * LunchRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class LunchRepository extends EntityRepository
{
    public function getActiveLunches()
    {
        $qb = $this->createQueryBuilder('l');

        $qb->where('l.active = :isActive')
            ->setParameter('isActive', true)
            ->orderBy('l.id');

        $query = $qb->getQuery();

        return $query->getResult();
    }

    public function getActiveCategoryAndDayLunches($category, $day)
    {
        $qb = $this->createQueryBuilder('l');

        $qb->where('l.active = :isActive')
            ->andWhere('l.categories = :category')
            ->andWhere('l.day = :day')
            ->setParameter('isActive', true)
            ->setParameter('category', $category)
            ->setParameter('day', $day);

        $query = $qb->getQuery();

        return $query->getResult();
    }

    public function getActiveLunchesById($id)
    {
        $qb = $this->createQueryBuilder('l');

        $qb->where('l.active = :isActive')
            ->andWhere('l.id = :id')
            ->setParameter('isActive', true)
            ->setParameter('id', $id);

        $query = $qb->getQuery();

        return $query->getResult();
    }


}